import React, { Component } from 'react'

class Container1 extends Component {

    constructor(props) {
        super(props);
        this.state = {
            level: "init"
        }
    }

    changeState = () => {
        this.setState(
            (prevState, props) =>
                (
                    {
                        level: prevState.level + " "+ "medium"
                    }
                )
        )
    }

    render() {
        return (
            <div>
                <button onClick={() => this.changeState()}>Change state</button>
                max is  {this.props.max} and type is {this.props.nick} state is {this.state.level}
            </div>
        );
    }
}



export default Container1;
